import Vue from 'vue'
import Vuex from 'vuex'
import axios from "axios";
// Plug-in installation vuex (also a plug-in for vue, need to use it for plug-in installation initialization)
Vue.use(Vuex)

// create store
const store = new Vuex.Store({
    // Fill in various Vuex data and methods here
    state: {
        model: {
            engineName: '',
            modelName: '',
            dataSetName: '',
            pronDictMapName: ''
        },
        file: {
            // Fill file information
            url:'',
            method:'',
            data:null,
            transformRequest:null
        },
        detail:{
            model_name:'',
            dataset_name:'',
            engine_name:'',
            pron_dict_name:'',
            status:'',


        }
    },
    mutations: {
        setModel(state,obj) {
            state.model.engineName=obj.engineName
            state.model.modelName=obj.modelName
            state.model.dataSetName=obj.dataSetName
            state.model.pronDictMapName=obj.pronDictMapName

        },
        setFile(state,obj) {
            state.model.url=obj.url
            state.model.method=obj.method
            state.model.data=obj.data
            state.model.transformRequest=obj.transformRequest

        },
        requestTranscribeResult(state){
            axios({
                url: 'http://localhost:8091/kalid/transcribe',
                params:{
                    model:state.model,
                    file:state.file
                }
            }).then(result => {
                console.log(result)
            }).catch(err => {
                console.log(err)
            })
        }

    }

})

// export store
export default store
